INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('HO', 'Hồng', 'img/Category/HO/HO.jpg', 'Trong ngày lễ tình nhân hay mỗi dịp kỉ niệm tình yêu, hoa hồng là một món quà không thể thiếu. Bởi lẽ đó là loại hoa tượng trưng cho tình yêu được nhiều người biết đến nhất trên thế giới, là loại hoa ngọt ngào, nồng thắm mà tình yêu đã len lỏi vào từng cánh hoa, nhị hoa và cả hương thơm dịu nhẹ phảng phất...');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('CC', 'Cẩm Chướng', 'img/Category/CC/CC.jpg', 'Hoa cẩm chướng là một trong những loài hoa đẹp được nhiều người yêu thích và đã xuất hiện từ lâu đời. Tuy nhiên, với ý nghĩa tượng trưng của mình, hoa cẩm chướng phần lớn được dành tặng cho bà , cho mẹ hoặc cho bạn bè. Rất ít người dùng hoa cẩm chướng để dành tặng người yêu.');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('HD', 'Hướng Dương', 'img/Category/HD/HD.jpg', 'Những đóa hoa hướng dương hệt như ánh mặt trời rực rỡ - chính vậy mà hướng dương được xem là loài hoa tượng trưng cho sự đam mê mãnh liệt.Một loài hoa tươi sáng và vui vẻ, mạnh mẽ nhưng cũng đầy dịu dàng và đam mê -Hướng Dương chính là một món quà bày tỏ sự quan tâm ấm áp nhất.');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('CU', 'Cúc', 'img/Category/CU/CU.jpg', 'Hoa cúc - loài hoa được người ta yêu mến bởi vẻ mộc mạc, giản đơn và biết bao gần gũi. Không chỉ là nét ngây thơ trong sáng của những đóa cúc nhỏ nhắn, trắng xinh; mà còn là niềm vui tươi, rạng rỡ của những đóa cúc đa sắc màu...');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('LA', 'Lan', 'img/Category/LA/LA.jpg', 'Phong lan đã được đề cao tử thời xa xưa bởi những điều mà nó tượng trưng.\n• Tình yêu\n• Sắc đẹp\n• Sự sinh sản\n• Sự thanh khiết\n• Sự thấu hiểu\n• Sự quyến rũ');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('HU', 'Huệ', 'img/Category/HU/HU.jpg', 'Hoa huệ và tất cả các dạng khác của nó, thường được xem xét mang các ý nghĩa sau đây:\r\nHoàng gia và vương giả\nThiên chức làm mẹ và khả năng sinh sản\nSự tinh khiết và vẻ đẹp tuổi trẻ\nLòng đam mê\nĐổi mới và tái sinh');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('TL', 'Tu-lip', 'img/Category/TL/TL.jpg', 'Tuy nó không phải là bông hoa kiêu hãnh nhất trong khu vườn, nhưng nét đẹp và vẻ duyên dáng của một bông hoa tulip đã trở thành biểu tượng cho những điều sau:\n\nSự hoàn hảo, tình yêu lâu dài giữa hai người hoặc các thành viên gia đình\nTình yêu say đắm, cho dù niềm đam mê bị từ chối hoặc trả lại\nTính hoàng tộc và vương giả\nTình yêu bị quên lãng\nKỉ niệm 11 năm ngày cưới\nSự phong phú, thịnh vượng, và niềm đam mê\nTừ thiện và hỗ trợ cho những người kém may mắn');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('LY', 'Ly', 'img/Category/LY/LY.jpg', 'Hoa Ly từ lâu đã được mệnh danh là một loài hoa thanh cao và quý phái, nó không những tượng trưng cho sắc đẹp, đức hạnh mà còn là sự kiêu hãnh và cả tình yêu cao thượng, chung thủy. Chính bởi vậy, hoa Ly không chỉ thích hợp để dành tặng mẹ, người yêu mà còn rất thích hợp cho ngày chúc mừng, tốt nghiệp, khai trương...');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('KH', 'Khác', 'img/Category/KH/KH.jpg', 'Các loại hoa khác.');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('SE', 'Sen', 'img/Category/SE/SE.jpg', 'Hoa sen chính là quốc hoa của đất nước Việt Nam thân yêu của chúng ta và từ rất lâu rồi đối với mỗi người dân đất Việt thì vẻ đẹp của hoa sen luôn làm say đắm lòng người. Không giống như những loài hoa bình thường khác hoa sen bình dị, thân thuộc nhưng vẫn rất tinh tế và có một ý nghĩa tuyệt vời riêng. Cũng giống như câu nói “Gần bùn nhưng chẳng hôi tanh mùi bùn”.\nChính vì lẽ ấy nên hoa sen luôn được nhiều người chọn lựa làm quà tặng trao gửi nhắn nhủ tình cảm thân thương dành cho nhau. Trao tặng một bó hoa sen chính là sự truyền tải thông điệp về lòng quý mến, ngưỡng mộ đến với một ai đó. Sắc hoa nhẹ nhàng tinh tế, hương thơm dịu dàng lan tỏa của những đóa hoa sen sẽ là quà tặng tuyệt vời làm ấm lòng người nhận.');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('CT', 'Cát Tường', 'img/Category/CT/CT.jpg', 'Hoa cát tường dịu êm chính là món quà tuyệt vời mà bạn không nên bỏ qua. Những cánh hoa mỏng manh tựa vẻ đẹp của người người phụ nữ nên việc tặng bó hoa cát tường sẽ chính là cách mà bạn bày tỏ tình cảm chân thành cũng như ca ngời về người ấy.');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('TT', 'Thạch Thảo', 'img/Category/TT/TT.jpg', 'Thạch thảo tượng trưng cho một tình yêu nhẹ nhàng và bình dị. ... Và, có một điều đặc biệt, dù thạch thảo gợi lên vẻ mỏng manh, có chút yếu đuối, nhưng đôi lúc, nó lại mang ý nghĩa của sự chín chắn.');
INSERT INTO `flowerdb`.`flower_cate` (`f_cate_ID`, `f_cate_name`, `f_cate_img`, `f_cate_detail`) VALUES ('CTC', 'Cẩm Tú Cầu', 'img/Category/CTC/CTC.jpg', 'Cẩm tú cầu với những cánh hoa mỏng manh, chen chúc- tạo thành một cầu hoa xinh xắn với những ý nghĩa rất riêng. Được nhiều người yêu thích lựa chọn để làm hoa trang trí đám cưới, hoặc hoa tặng trong những dịp lễ, kỉ niệm.');

