INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO00', 'HO', 'Hoa hồng đỏ', 'img/Flower/HHO00/HHO00.jpg', 'Hoa hồng đỏ: tượng trưng cho một tình yêu nồng cháy và lãng mạn. Hoa hồng đỏ đã có từ rất lâu đời, trải qua nhiều nền văn hóa - cả phương tây và phương đông.Nhưng ở bất kì đâu, nó cũng luôn là loài hoa được yêu thích nhất, và biểu tượng cho một thứ tình cảm vô cùng thiêng liêng: Tình yêu.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO01', 'HO', 'Hoa hồng Đà Lạt hồng', 'img/Flower/HHO01/HHO01.jpg', 'Hoa hồng Đà Lạt với màu hồng phấn dịu nhẹ của hoa mang đến thông điệp về sự trìu mến, về một tình yêu lãng mạn và nhẹ nhàng.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO02', 'HO', 'Hoa hồng nhung', 'img/Flower/HHO02/HHO02.jpg', 'Hoa hồng nhung tượng trưng cho một tình yêu say đắm và nồng nhiệt.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO03', 'HO', 'Hoa hồng cổ Sapa', 'img/Flower/HHO03/HHO03.jpg', 'Hoa hồng cổ Sapa với màu hồng phấn dịu nhẹ của hoa mang đến thông điệp về sự trìu mến, về một tình yêu lãng mạn và nhẹ nhàng.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO04', 'HO', 'Hoa hồng cổ Vân Khôi', 'img/Flower/HHO04/HHO04.jpg', 'Hoa hồng cổ Vân Khôi với màu trắng pha hồng nhẹ tượng trưng cho sự ngây thơ, duyên dáng và sự cảm thông. Hồng trắng không chỉ biểu tượng cho một tình yêu thuần khiết, cao thượng mà mặt khác, nó còn là loài hoa dành để bày tỏ lời xin lỗi đến người yêu nếu chẳng may bạn có mắc lỗi lầm.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO05', 'HO', 'Hoa hồng leo tầm xuân', 'img/Flower/HHO05/HHO05.jpg', 'Hoa hồng leo tầm xuân với màu hồng phớt nhẹ nhàng của hoa hồng ngụ ý về sự bắt đầu của một tình yêu đầy mơ mộng, lãng mạn và dịu dàng. Bên cạnh đó, nó còn thể hiện một hạnh phúc tròn đầy với lời nhắn nhủ: \"Hãy tin anh nhé! \"');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO06', 'HO', 'Hoa hồng trắng', 'img/Flower/HHO06/HHO06.jpg', 'Hoa hồng trắng tượng trưng cho sự ngây thơ, duyên dáng và sự cảm thông. Hồng trắng không chỉ biểu tượng cho một tình yêu thuần khiết, cao thượng mà mặt khác, nó còn là loài hoa dành để bày tỏ lời xin lỗi đến người yêu nếu chẳng may bạn có mắc lỗi lầm.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHD00', 'HD', 'Hoa hướng dương vàng', 'img/Flower/HHD00/HHD00.jpg', 'Với đặc tính hướng quang - luôn hướng về phía mặt trời- hoa hướng dương biểu trưng cho lòng trung thành, tính kiên định. Với tình yêu, đó là biểu tượng của sự chung thủy, sắt son.\n\nVà, hình dáng những cánh hoa vàng rực rỡ như ánh mặt trời, khiến người nhìn cảm thấy tràn đầy năng lượng ấy- là biểu hiện của sự quan tâm chân thành, ấm áp và là niềm vui, hạnh phúc lan tỏa.\n\nBên cạnh đó, hoa hướng dương còn có ý nghĩa là sự ngưỡng mộ, tôn sùng và sự bền vững lâu dài. \nChính vì vậy, hoa hướng dương thích hợp để bày tỏ tình yêu dài lâu, cũng như những dịp chúc mừng, kỉ niệm để gửi đi thông điệp về sự hân hoan, phấn khởi.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHD01', 'HD', 'Hoa hướng dương lùn đỏ', 'img/Flower/HHD01/HHD01.jpg', 'Với đặc tính hướng quang - luôn hướng về phía mặt trời- hoa hướng dương biểu trưng cho lòng trung thành, tính kiên định. Với tình yêu, đó là biểu tượng của sự chung thủy, sắt son.\n\nVà, hình dáng những cánh hoa vàng rực rỡ như ánh mặt trời, khiến người nhìn cảm thấy tràn đầy năng lượng ấy- là biểu hiện của sự quan tâm chân thành, ấm áp và là niềm vui, hạnh phúc lan tỏa.\n\nBên cạnh đó, hoa hướng dương còn có ý nghĩa là sự ngưỡng mộ, tôn sùng và sự bền vững lâu dài. \nChính vì vậy, hoa hướng dương thích hợp để bày tỏ tình yêu dài lâu, cũng như những dịp chúc mừng, kỉ niệm để gửi đi thông điệp về sự hân hoan, phấn khởi.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HLA00', 'LA', 'Hoa lan hồ điệp trắng', 'img/Flower/HLA00/HLA00.jpg', 'Hoa lan hồ điệp trắng là biểu hiện của sự trong trắng, tinh khiết, thánh thiện và trinh nguyên.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HLA01', 'LA', 'Hoa lan hồ điệp hồng', 'img/Flower/HLA01/HLA01.jpg', 'Hoa lan hồ điệp hồng có thể được dành tặng dưới dạng một bó hoa với những cành hồ điệp được cắt tuyển, chọn lọc. Cũng có thể là một chậu lan để có thể chăm sóc hằng ngày. \nVà dù với hình thức nào đi chăng nữa, người yêu hoa, người tặng hoa và cả người nhận hoa, cũng luôn quan tâm đến ý nghĩa mà cành hoa thể hiện.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HLA02', 'LA', 'Hoa lan hồ điệp đỏ', 'img/Flower/HLA02/HLA02.jpg', 'Hoa lan hồ điệp đỏ có thể được dành tặng dưới dạng một bó hoa với những cành hồ điệp được cắt tuyển, chọn lọc. Cũng có thể là một chậu lan để có thể chăm sóc hằng ngày. \nVà dù với hình thức nào đi chăng nữa, người yêu hoa, người tặng hoa và cả người nhận hoa, cũng luôn quan tâm đến ý nghĩa mà cành hoa thể hiện.\n\n');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HLA03', 'LA', 'Hoa lan hồ điệp vàng', 'img/Flower/HLA03/HLA03.jpg', 'Đối với hoa lan hồ điệp vàng, người ta gửi gắm vào đó sự ấm áp của tình thân, sự cầu chúc sung túc, thành công, và cả vẻ đẹp thanh cao, sang trọng.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HSE00', 'SE', 'Hoa Sen Hồng', 'img/Flower/HSE00/HSE00.jpg', 'Hoa sen luôn được nhiều người chọn lựa làm quà tặng trao gửi nhắn nhủ tình cảm thân thương dành cho nhau. Trao tặng một bó hoa sen chính là sự truyền tải thông điệp về lòng quý mến, ngưỡng mộ đến với một ai đó. Sắc hoa nhẹ nhàng tinh tế, hương thơm dịu dàng lan tỏa của những đóa hoa sen sẽ là quà tặng tuyệt vời làm ấm lòng người nhận.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HCT00', 'CT', 'Hoa Cát Tường tím', 'img/Flower/HCT00/HCT00.jpg', 'Hoa cát tường dịu êm chính là món quà tuyệt vời mà bạn không nên bỏ qua. Những cánh hoa mỏng manh tựa vẻ đẹp của người người phụ nữ nên việc tặng bó hoa cát tường sẽ chính là cách mà bạn bày tỏ tình cảm chân thành cũng như ca ngời về người ấy.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HTT00', 'TT', 'Hoa Thạch Thảo tím', 'img/Flower/HTT00/HTT00.jpg', 'Thạch thảo tượng trưng cho một tình yêu nhẹ nhàng và bình dị. ... Và, có một điều đặc biệt, dù thạch thảo gợi lên vẻ mỏng manh, có chút yếu đuối, nhưng đôi lúc, nó lại mang ý nghĩa của sự chín chắn.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HCTC00', 'CTC', 'Hoa Cẩm Tú Cầu xanh', 'img/Flower/HCTC00/HCTC00.jpg', 'Theo một truyền thuyết Nhật Bản, một hoàng đế tự hào sử dụng một bó hoa cẩm tú cầu như là một lời xin lỗi đến gia đình của một cô gái mà ông đang yêu. Mãi mãi về sau, những bông hoa đã phục vụ như là một đại diện của tình cảm chân thành, cho dù niềm vui hay nỗi buồn');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HCTC01', 'CTC', 'Hoa Cẩm Tú Cầu trắng', 'img/Flower/HCTC01/HCTC01.jpg', 'Cẩm tú cầu với những cánh hoa mỏng manh, chen chúc- tạo thành một cầu hoa xinh xắn với những ý nghĩa rất riêng. Được nhiều người yêu thích lựa chọn để làm hoa trang trí đám cưới, hoặc hoa tặng trong những dịp lễ, kỉ niệm.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO07', 'HO', 'Hoa Hồng Da', 'img/Flower/HHO07/HHO07.jpg', 'Hoa hồng trắng là biểu tượng của sự thuần khiết, trong sáng. Tặng một bông hoa hồng trắng cho người mà bạn yêu thương sẽ gửi đi thông điệp rằng “Bạn rất trân trọng tình yêu của 2 người và mong muốn tình yêu của 2 người sẽ kéo dài mãi mãi”.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HLA04', 'LA', 'Hoa Lan Vũ Nữ vàng', 'img/Flower/HLA04/HLA04.jpg', 'Hoa Phong Lan Vũ Nữ chinh phục người yêu hoa bởi vẻ đẹp tựa hồ vũ nữ, dập dìu như đàn bướm. Vẻ vui tươi, tràn đầy sức sống cùng hương thơm phảng phất dịu nhẹ của cây khiến bao người mê đắm. Đây chắc chắn sẽ là món quà đầy ý nghĩa nếu bạn muốn tìm một món quà tặng nhân dịp khai trương, khánh thành các cơ sở kinh doanh của người thân, bạn bè.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HTT01', 'TT', 'Hoa Thạch Thảo trắng', 'img/Flower/HTT01/HTT01.jpg', 'Thạch thảo tượng trưng cho một tình yêu nhẹ nhàng và bình dị. Trong tình yêu ấy, tình cảm đong đầy và luôn ẩn hiện đôi mắt dõi theo nhau, những lời hứa hẹn sẽ không bao giờ quên, sẽ mãi mãi đi cùng nhau trên những sóng gió, gập ghềnh...');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO08', 'HO', 'Hoa Hồng tím', 'img/Flower/HHO08/HHO08.jpg', 'Nếu như hoa hồng đỏ thể hiện tình yêu mãnh liệt, nồng cháy; hồng trắng là tình yêu trong sáng, ngây thơ; hồng vàng dành cho sự phản bội… thì ý nghĩa hoa hồng tím là sự trường tồn, vĩnh cửu.');
INSERT INTO `flowerdb`.`flower` (`f_ID`, `f_cate_ID`, `f_name`, `f_img`, `f_detail`) VALUES ('HHO09', 'HO', 'Hoa Hồng xanh', 'img/Flower/HHO09/HHO09.png', 'Màu xanh dương là màu của trời và biển, màu của sự yên bình và vĩnh cửu. Vì thế, ý nghĩa của hoa hồng xanh là biểu tượng của tình yêu bất diệt, chung thủy, trường tồn theo thời gian. Bên cạnh đó, trong tình yêu, hoa hồng xanh còn thể hiện sự bí ẩn, huyền bí.');

