USE `flowerdb`;
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B000', 'Ngọt ngào và bình yên', 'Bó hoa sen hồng Ngọt ngào và bình yên chính là món quà tuyệt vời mà Hoa Sài Gòn muốn nhắn gửi đến quý khách. Với vẻ đẹp dịu dàng, tĩnh lặng và cũng vô cùng tinh tế này bạn có thể dùng nó để tặng sinh nhật những người thân yêu hoặc cũng có thể dùng làm hoa trong các dịp sự kiện khác nhau. Bó hoa là sự kết hợp giữa 10 đóa sen hồng cùng với đài sen và lá phụ kiện.', 300000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B001', 'Tím dịu êm', 'Tím dịu êm là sự kết hợp giữa rất nhiều đóa hoa cát tường tím dịu êm cùng với những đóa thạch thảo tím để nên một tổng thể hài hòa, đẹp mắt. Ngoài ra nhờ lớp giấy gói tông hồng paste và chiếc nơ cùng tone còn giúp cho bó hoa trở nên xinh đẹp và rạng rỡ hơn.', 500000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B002', 'Ngày tươi đẹp', 'Ngày tươi đẹp chính là món quà tuyệt vời để bạn dành tặng cho những người thân yêu nhân dịp sinh nhật. Bó được là sự kết hợp giữa những đóa hoa hướng dương rực rỡ cùng những đóa hoa hồng đỏ kiêu sa và những bông hoa, lá phụ khác. Tất cả hướng đến lời chúc về một ngày tươi đẹp rực rỡ!', 500000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B003', 'Khúc dương cầm', 'Bó hoa đại biểu Khúc dương cầm chính là món quà ý nghĩa thân thương mà chúng ta có thể dành tặng vào nhiều dịp khác nhau. Bó hoa là sự kết hợp giữa 7 đóa hoa hướng dương tươi thắm rạng rỡ, cùng với những chiếc lá xanh phụ kiện. Tất cả tạo nên một sản phẩm đẹp mắt, tinh tế và vô cùng sang trọng.', 400000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B004', 'Chỉ yêu mình em', 'Vào dịp lễ Valentine 14/2, ngoài socola thì hoa hồng chính là món quà hoàn hảo để các đấng mày râu dành tặng cho nửa kia của mình. Những đóa hoa hồng đỏ với màu sắc quyến rũ sẽ giúp bạn truyền tải thông điệp yêu thương cho ngày valentine thêm ý nghĩa.\nBó Chỉ yêu mình em gồm 15 đóa hồng đỏ kết hợp với hoa thanh liễu trắng, lá chanh cùng một số lá phụ nhập khác, giấy gói tone màu sang trọng tạo ra một món quà hoàn hảo.', 520000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B005', 'Thanh khiết', 'Bó hoa sinh nhật Thanh khiết là một sự kết hợp giữa cẩm tú cầu xanh nhập khẩu, những bông hồng da cùng những bông hoa phụ màu xanh lơ.', 400000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B006', 'Thơ ngây', 'Giỏ hoa Thơ ngây là thiết kế dành cho những cô nàng có phong cách hiện đại và thơ ngây. Mẫu hoa với sự kết hợp của hồng đỏ rực rỡ, hồng da tươi thắm hòa quyện cùng màu vàng tươi mới của lan vũ nữ.', 550000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B007', 'Tình đậm sâu ', 'Bó hoa hồng đẹp tặng sinh nhật xinh đẹp Tình đậm sâu bao gồm 17 đóa hoa hồng thắm tươi xinh kết hợp cùng với những đóa hoa thạch thảo và cành lá phụ màu xanh. Bó hoa được bó với lớp giấy báo bên trong cùng giấy lưới bên ngoài tạo nên vẻ đẹp vô cùng tinh tế và sang trọng.', 550000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B008', 'Hạnh phúc giản đơn', 'Hộp hoa sinh nhật bằng gỗ Hạnh phúc giản đơn chính là sự kết hợp giữa 3 đóa hoa hồng da cùng 3 đóa hoa hồng tím to, đẹp cùng hương thơm quyến rũ. Xen kẽ vào đó là những cành hoa lá phụ kiện như thạch thảo tím và hoa mimi trắng. Đặc biệt món quà càng trở nên độc đáo hơn khi có sự hiện diện của cặp đôi gấu vô cùng đáng yêu.', 550000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B009', 'Tia nắng trong anh', 'Bó hoa chúc mừng tươi thắm rạng rỡ Tia nắng trong anh là món quà ngọt ngào mà chúng ta có thể dành tặng cho những người thân yêu nhân ngày sinh nhật. Tia nắng trong anh đó chính là sự kết hợp giữa 6 đóa hoa hướng dương đẹp và tươi thắm rạng rỡ kèm theo lớp giấy gói màu đen sang trọng. Chắc chắn rằng món quà tuyệt vời này sẽ khiến cho người nhận hạnh phúc ngất ngây.', 300000, 1);
INSERT INTO `flowerdb`.`bouquet` (`b_ID`, `b_name`, `b_detail`, `b_price`, `b_selling`) VALUES ('B010', 'Kẻ mộng mơ', 'Đây chính là món quà tuyệt diệu với sự kết hợp của 56 đóa hoa hồng trắng tinh khôi và bông hồng xanh phun sơn dịu dàng. Bó hoa của những màu sắc dịu dàng đặc biệt cuốn hút làm say đắm lòng người.', 1400000, 1);

