INSERT INTO `flowerdb`.`occasion` (`occa_ID`, `occa_name`, `occa_detail`, `occa_fp`, `occa_img`) VALUES ('CM', 'Chúc Mừng', 'Sự lựa chọn tuyệt vời khi muốn tặng quà cho người thân, bạn bè bởi trước những sắc hoa rực rỡ thì ai cũng sẽ cảm thấy hạnh phúc, vui vẻ.\nHoa chúc mừng không chỉ giúp tỏ đẹp thêm không gian nơi bạn đặt nó mà quan trọng hơn, những lằng hoa đó còn như thay lời muốn nói và thể hiện tình cảm của người tặng cho người nhận.', 1, 'img/Occasion/CM.jpg');
INSERT INTO `flowerdb`.`occasion` (`occa_ID`, `occa_name`, `occa_detail`, `occa_fp`, `occa_img`) VALUES ('TY', 'Tình Yêu', 'Sự lựa chọn hàng đầu giúp các cặp đôi thể hiện tình cảm.\nValentine hay ngày kỷ niệm, không chỉ hoa Hồng Đỏ,\nHoa Cúc, Tulip hay Cẩm Chướng sẽ thể hiện được tình cảm nồng nàn của những cặp đôi yêu nhau.', 1, 'img/Occasion/TY.jpg');
